mapper.insert:
INSERT INTO BOARD (
	BOARD_NUM
	, TITLE
	, CONTENT
	, WRITER
	, IS_PRIVATE
) VALUES (
	(SELECT NVL(MAX(BOARD_NUM), 0) + 1 FROM BOARD)
	, #{title}
	, #{content}
	, #{writer}
	, 'N'
)

INSERT INTO REPLY (
	REPLY_NUM
	, CONTENT
	, WRITER
	, BOARD_NUM
) VALUES (
	(SELECT NVL(MAX(REPLY_NUM), 0) + 1 FROM REPLY)
	, #{content}
	, #{writer}
	, #{boardNum}
)
